INSERT INTO activity (name, description, type) VALUES ("Mig og min Voksne ( 1 - 2 år)", "Med træningen vil både barnet og den voksne blive udfordret på lege, balance og springbaner (alt efter barnets alder), så alle kommer til at deltage aktiv. Børn som voksne, møder derfor op i træningstøj.", "ACROBATICS");
INSERT INTO activity (name, description, type) VALUES ("Mig og min Voksne ( 3 - 4 år)", "Med træningen vil både barnet og den voksne blive udfordret på lege, balance og springbaner (alt efter barnets alder), så alle kommer til at deltage aktiv. Børn som voksne, møder derfor op i træningstøj.", "ACROBATICS");
INSERT INTO activity (name, description, type) VALUES ("HGF Piger ( 5 - 7 år ) hold 1", "Dette hold er for piger, der gerne vil prøve kræfter med rytme/og springgymnastik. Gennem sæsonen træner vi grundelementer og grundbasis for en træningstime, det vil sige opvarmning, træne lille rytmeserie og små spring på banerne i springcenteret.", "ACROBATICS");
INSERT INTO activity (name, description, type) VALUES ("HGF Styrke og Motion", "Sæson slutter ved udgangen af april 2024. På Styrke & motion kan du vælge at gå 1 eller 2 gange om ugen. Vi træner mandag og onsdag. Tilmelder du dig 1 gang om ugen vælger du selv uge for uge, hvilken dag der passer dig bedst. Mandage træner vi 1 time fra 16.45 til 17.45. Onsdage i 1 1⁄2 time fra 18.00 til 19.30", "FITNESS");
INSERT INTO activity (name, description, type) VALUES ("HGF DameGym", "Sæson slutter ved udgangen af april 2024. På holdet arbejder vi med opstrammende og løsnende øvelser for hele kroppen. Med hensyn til træning i efterårs/vinterferie aftale vi, om der skal trænes, eller om der skal holdes ferie.", "FITNESS");
INSERT INTO activity (name, description, type) VALUES ("HGF ManneGym", "Sæson slutter ved udgangen af april 2024. Holdene er ”kun for mænd”, såvel yngre som dem i deres bedste alder. Der er tilbud og udfordringer for alle uanset alder, form og behov.", "FITNESS");